#include <iostream>
using namespace std;

int main()
{
    int a, b;
    a = 10;
    b = 20;

    cout << "Addition is : " << a + b;
    cout << "\nSubstraction is : " << a - b;
    cout << "\nMultiplication is : " << a * b;
    cout << "\nDivision is : " << a / b;
    cout << "\nRemainder is : " << a % b;
}