#include <iostream>
using namespace std;
int main()
{
    int var1 = 9, var2 = 18;

    cout << "var1 > var2 : " << (var1 > var2);
    cout << "\nvar1 < var2 : " << (var1 < var2);
    cout << "\nvar1 >= var2 : " << (var1 >= var2);
    cout << "\nvar1 <= var2 : " << (var1 <= var2);
    cout << "\nvar1 == var2 : " << (var1 == var2);
    cout << "\nvar1 != var2 : " << (var1 != var2);
}